mapper.select:
SELECT COUNT(t3.ID_) FROM t_vacation_task t, t_flow_main t2, act_ru_task t3
t3.ASSIGNEE_ = #{userId}
AND t3.NAME_ like '%${name}%'
AND t.state = 1 AND t.id = t2.task_id AND t2.flow_id = t3.PROC_INST_ID_
AND t3.CREATE_TIME_ >= #{startTime}
AND t3.CREATE_TIME_ <= #{endTime}

SELECT t.id id, t.user_id userId, t.start_time startTime, t.end_time endTime, t.title title, t.context context, t2.flow_def_code flowDefCode, t3.ID_ taskId, t3.NAME_ taskName, t3.PROC_INST_ID_ flowId, t3.TASK_DEF_KEY_ taskDefKey, t3.ASSIGNEE_ assign, t3.CREATE_TIME_ createTime FROM t_vacation_task t, t_flow_main t2, act_ru_task t3
t3.ASSIGNEE_ = #{userId}
AND t3.NAME_ like '%${name}%'
AND t.state = 1 AND t.id = t2.task_id AND t2.flow_id = t3.PROC_INST_ID_
AND t3.CREATE_TIME_ >= #{startTime}
AND t3.CREATE_TIME_ <= #{endTime}
ORDER BY t3.CREATE_TIME_ DESC LIMIT ${startRow}, ${offset}
