mapper.select:
SELECT COUNT(t3.ID_)
FROM t_vacation_process t,
t_flow_main t2,
act_ru_task t3

    t.id = t2.process_id
    AND t2.flow_id = t3.PROC_INST_ID_

        AND t3.ASSIGNEE_ = #{userId}


        AND t.state = #{state}


        AND t.title like '%${title}%'


        AND t3.CREATE_TIME_ >= #{startTime}


        AND t3.CREATE_TIME_ <= #{endTime}

SELECT t.id id, t.flow_def_id flowDefId, t.applier_id applierId, t.assignee_id assigneeId, t.start_time startTime, t.end_time endTime, t.title title, t.context context, t.create_time creTm, t3.ID_ taskId, t3.NAME_ taskName, t3.PROC_INST_ID_ flowId, t3.TASK_DEF_KEY_ taskDefKey, t3.ASSIGNEE_ assign FROM t_vacation_process t, t_flow_main t2, act_ru_task t3
t.id = t2.process_id AND t2.flow_id = t3.PROC_INST_ID_
AND t3.ASSIGNEE_ = #{userId}
AND t.state = #{state}
AND t.title like '%${title}%'
AND t3.CREATE_TIME_ >= #{startTime}
AND t3.CREATE_TIME_ <= #{endTime}
ORDER BY t3.CREATE_TIME_ DESC LIMIT ${startRow}, ${offset}
