mapper.select:
SELECT COUNT(t.id)
FROM t_vacation_task t
LEFT JOIN t_sys_userinfo t2 ON t.user_id = t2.id


        t.user_id = #{userId}


        AND t.title like '%${title}%'


        AND t.create_time >= #{startTime}


        AND t.create_time <= #{endTime}

SELECT t.id id, t.user_id userId, t.type type, t.title title, t.context context, t.start_time startTime, t.end_time endTime, t.state state, t.create_time creTm, t.update_time updTm, t2.name userName FROM t_vacation_task t LEFT JOIN t_sys_userinfo t2 ON t.user_id = t2.id
t.user_id = #{userId}
AND t.title like '%${title}%'
AND t.create_time >= #{startTime}
AND t.create_time <= #{endTime}
ORDER BY t.create_time DESC LIMIT ${startRow}, ${offset}
