mapper.select:
SELECT COUNT(*)
FROM t_sys_job t


        t.dept_id IN

            #{deptId}



        AND t.name like '%${name}%'


        AND t.create_time >= #{startTime}


        AND t.create_time <= #{endTime}


        AND t.enabled = #{enabled}

SELECT t.id id, t.name name, t.dept_id deptId, t.sort sort, t.enabled enabled, t.create_time creTm, t1.name deptName, t1.pid deptPid FROM t_sys_job t LEFT JOIN t_sys_dept t1 ON t1.id = t.dept_id
t.dept_id IN
#{deptId}
AND t.name like '%${name}%'
AND t.create_time >= #{startTime}
AND t.create_time <= #{endTime}
AND t.enabled = #{enabled}
ORDER BY t.sort ASC LIMIT ${startRow}, ${offset}

SELECT t.id id, t.name name, t.dept_id deptId, t.sort sort, t.enabled enabled, t.create_time creTm FROM t_sys_job t
t.dept_id = #{deptId}
ORDER BY t.sort ASC
