mapper.select:
SELECT DISTINCT t.id id, t.pid pid, t.`name` `name`, t.path path, t.component component, t.icon icon, t.sort sort, t.i_frame iFrame, t.cache cache, t.hidden hidden, t.create_time creTm FROM t_sys_menu t INNER JOIN t_sys_role_menu t1 on t1.menu_id = t.id
t.type != #{type}
AND t1.role_id IN
#{rolePo.id}
ORDER BY t.sort ASC

SELECT DISTINCT t.id id, t.pid pid, t.`name` `name`, t.path path, t.component component, t.icon icon, t.sort sort, t.i_frame iFrame, t.cache cache, t.hidden hidden, t.permission permission, t.type type, t.create_time creTm FROM t_sys_menu t INNER JOIN t_sys_role_menu t1 on t1.menu_id = t.id
t1.role_id = #{roleId}
ORDER BY t.sort ASC

SELECT DISTINCT t.id id, t.pid pid, t.`name` `name`, t.path path, t.component component, t.icon icon, t.sort sort, t.i_frame iFrame, t.cache cache, t.hidden hidden, t.permission permission, t.type type, t.create_time creTm FROM t_sys_menu t
t.pid = #{pid}
ORDER BY t.sort ASC

SELECT t.id id, t.pid pid, t.`name` `name`, t.path path, t.component component, t.icon icon, t.sort sort, t.i_frame iFrame, t.cache cache, t.hidden hidden, t.permission permission, t.type type, t.create_time creTm FROM t_sys_menu t
t.name like '%${name}%'
AND t.create_time >= #{startTime}
AND t.create_time <= #{endTime}
ORDER BY t.sort ASC
