mapper.select:
SELECT DISTINCT t.id id, t.pid pid, t.`name` `name`, t.path path, t.component component, t.icon icon, t.sort sort, t.i_frame iFrame FROM t_sys_menu t INNER JOIN t_sys_role_menu t1 on t1.menu_id = t.id
t.type != #{type}
AND t1.role_id IN
#{rolePo.id}
ORDER BY t.sort ASC

SELECT DISTINCT t.id id, t.pid pid, t.`name` `name`, t.path path, t.component component, t.icon icon, t.sort sort, t.i_frame iFrame, t.permission permission, t.type type FROM t_sys_menu t INNER JOIN t_sys_role_menu t1 on t1.menu_id = t.id
t1.role_id = #{roleId}
ORDER BY t.sort ASC

SELECT DISTINCT t.id id, t.pid pid, t.`name` `name`, t.path path, t.component component, t.icon icon, t.sort sort, t.i_frame iFrame, t.permission permission, t.type type FROM t_sys_menu t
t.pid = #{pid}
ORDER BY t.sort ASC
