mapper.select:
SELECT COUNT(*)
FROM t_sys_userinfo t


        t.dept_id IN

            #{deptId}



        AND t.name like '%${name}%' OR t.email like '%${name}%'


        AND t.create_time >= #{startTime}


        AND t.create_time <= #{endTime}


        AND t.enabled = #{enabled}

SELECT t.id id, t.account_id accountId, t.`name` `name`, t.phone phone, t.email email, t.dept_id deptId, t.job_id jobId, t.`password` `password`, t.token token, t.avatar avatar, t.enabled enabled, t.last_password_reset_time lastPasswordResetTime, t.create_time creTm FROM t_sys_userinfo t
t.dept_id IN
#{deptId}
AND t.name like '%${name}%' OR t.email like '%${name}%'
AND t.create_time >= #{startTime}
AND t.create_time <= #{endTime}
AND t.enabled = #{enabled}

mapper.delete:
DELETE FROM t_sys_user_role


        role_id IN

            #{roleId}



        AND user_id = #{userId}
